title=#{title}, author=#{author}, excerpt=#{excerpt}, image_thumb=#{imageThumb}, content=#{content}, tag_string=#{tagStrings}, article_status=#{articleStatus},
created_date = #{createdDate},
modified_date=#{modifiedDate}, comment_count=#{commentCount}, like_count=#{likeCount}, read_count=#{readCount}, istop=#{isTop}, recommendation=#{recommendation}, original=#{original}, original_url=#{originalUrl}, user=#{user.id}, category=#{category.id}
WHERE id=#{id}
