-- ----------------------------
--  Table structure for `article`
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `wid` bigint(20) NOT NULL DEFAULT '0' COMMENT 'WID',
  `user` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '作者ID',
  `category` bigint(20) unsigned NOT NULL COMMENT '分类ID',
  `created_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '创建时间',
  `title` text NOT NULL COMMENT '标题',
  `author` varchar(40) NOT NULL DEFAULT '佚名' COMMENT '文章作者',
  `excerpt` varchar(512) NOT NULL COMMENT '摘要',
  `image_thumb` mediumtext COMMENT '文章缩略图',
  `content` longtext NOT NULL COMMENT '文章内容',
  `tag_string` varchar(100) NOT NULL DEFAULT '' COMMENT '文章标签',
  `article_status` tinyint(20) NOT NULL DEFAULT '1' COMMENT '文章状态',
  `comment_status` varchar(20) NOT NULL DEFAULT 'OPEN' COMMENT '评论状态',
  `modified_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  `comment_count` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '评论人数',
  `like_count` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '喜欢人数',
  `read_count` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '阅读人数',
  `istop` tinyint(11) unsigned NOT NULL DEFAULT '0' COMMENT '是否置顶',
  `recommendation` tinyint(11) NOT NULL DEFAULT '0' COMMENT '是否推荐',
  `original` varchar(100) NOT NULL DEFAULT '琅嬛福地整理' COMMENT '文章出处',
  `original_url` varchar(100) DEFAULT NULL COMMENT '原文地址',
  PRIMARY KEY (`id`),
  KEY `user` (`user`),
  KEY `category` (`category`),
  CONSTRAINT `article_ibfk_1` FOREIGN KEY (`user`) REFERENCES `user` (`id`),
  CONSTRAINT `article_ibfk_2` FOREIGN KEY (`category`) REFERENCES `category` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

