-- ----------------------------
--  Table structure for `album`
-- ----------------------------
DROP TABLE IF EXISTS `album`;
CREATE TABLE `album` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(100) NOT NULL COMMENT '标题',
  `description` varchar(150) NOT NULL COMMENT '描述',
  `coverpic` varchar(100) NOT NULL COMMENT '封面',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `gallery`
-- ----------------------------
DROP TABLE IF EXISTS `gallery`;
CREATE TABLE `gallery` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(40) NOT NULL COMMENT '名称',
  `size` int(11) NOT NULL DEFAULT '0' COMMENT '图片大小',
  `path` varchar(100) NOT NULL COMMENT '图片路径',
  `thumb` varchar(100) NOT NULL COMMENT '图片素略图',
  `album` bigint(20) unsigned DEFAULT NULL COMMENT '图片所属相册',
  PRIMARY KEY (`id`),
  KEY `album` (`album`) USING BTREE,
  CONSTRAINT `gallery_ibfk_1` FOREIGN KEY (`album`) REFERENCES `album` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `novelinfo`
-- ----------------------------
DROP TABLE IF EXISTS `novelinfo`;
CREATE TABLE `novelinfo` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL COMMENT '小说名称',
  `author` varchar(30) NOT NULL COMMENT '作者',
  `description` text NOT NULL COMMENT '描述',
  `type` varchar(20) NOT NULL COMMENT '小说类型',
  `cover` varchar(100) DEFAULT NULL COMMENT '封面',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `novelchapter`
-- ----------------------------
DROP TABLE IF EXISTS `novelchapter`;
CREATE TABLE `novelchapter` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `wid` bigint(20) NOT NULL COMMENT '章节序列号',
  `title` varchar(50) NOT NULL COMMENT '章节标题',
  `location` varchar(100) NOT NULL COMMENT 'txt位置',
  `novel` bigint(20) unsigned DEFAULT NULL COMMENT '章节所属',
  PRIMARY KEY (`id`),
  KEY `nid` (`novel`),
  CONSTRAINT `dd` FOREIGN KEY (`novel`) REFERENCES `novelinfo` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

